pragma solidity ^0.4.21;


import 'openzeppelin-solidity/contracts/ownership/Ownable.sol';
import './Token.sol';

contract Test is Ownable {

  MintableToken public token;
  address wallet;
  bool bTotal = false;
  uint256 total;

  constructor (MintableToken _token) 
  {    
     token = _token;


  }

