      if (bTotal)
      {
        uint256 totalSupply = getTotalSupply() +_tokenAmount;
        require(total >= totalSupply);
      }

      MintableToken(address(token)).mint(_beneficiary, _tokenAmount);
  } 

function destroyCrowdsale() onlyOwner payable {  
    selfdestruct(wallet);    
  }

  function destroyToken() onlyOwner payable {
   Token(address(token)).destroy(wallet);
  }


  function getTotalSupply() view returns (uint) {      
    return MintableToken(address(token)).totalSupply();
  }

